<?php $this->load->view('header'); ?><?php $title="";$content="";$statuss="";$languages = "";$slug="";$button = "Submit";$action = "#";if(isset($pagedetails)){	$title = $pagedetails->page_title;	$content = $pagedetails->content;	$statuss = $pagedetails->status_id;	$languages = $pagedetails->language_id;	$slug = $pagedetails->slug;	$button="Update";	$action = base_url()."admin/update/".$pagedetails->id;} ?><div class="container" style="margin-left:15%;">	<div class="row">		<div class="col-sm-8 col-sm-offset-2" style="margin-top: 5%;">				<form action="<?= $action ?>" method="post">			<label>Title</label>			<input type="text" class="form-control" name="page_title" value="<?= $title ?>">						<label>Content</label>			<textarea cols="80" id="edi" name="content" rows="10" class="form-control">                <?= $content ?>            </textarea>            <script>				CKEDITOR.replace('edi');			</script>						<label>Status</label>			<select name="status_id" class="form-control">				<?php				for($i=0;$i<sizeof($status);$i++){					?>					<option <?php if($statuss == $status[$i]->id){ echo "selected"; } ?> value="<?= $status[$i]->id ?>"><?= $status[$i]->name ?></option>					<?php				}				?>			</select>						<label>Language</label>			<select name="language_id" class="form-control">				<?php				for($i=0;$i<sizeof($language);$i++){					?>					<option <?php if($languages == $language[$i]->id){ echo "selected"; } ?> value="<?= $language[$i]->id ?>"><?= $language[$i]->language_name ?></option>					<?php				}				?>			</select>						<label>Slug Name</label>			<input type="text" class="form-control" name="slug" value="<?= $slug ?>">						<input type="submit" value="<?= $button ?>" class="btn btn-primary center-block" style="margin-top:15px;">		</form>			</div>	</div></div>